const data = getWorkflowStaticData('global');
const channelId = String($env.DISCORD_QUESTIONS_CHANNEL_ID || '');
const key = `lastSeen:${channelId}`;
const lastSeen = data[key] ? String(data[key]) : null;

const inputItems = $input.all();
const normalized = inputItems
  .map((item) => {
    const msg = item.json || {};
    const author = msg.author || msg.user || {};
    return {
      id: msg.id ? String(msg.id) : (msg.message_id ? String(msg.message_id) : ''),
      content: typeof msg.content === 'string' ? msg.content : '',
      author_bot: Boolean(author.bot),
      author_id: author.id ? String(author.id) : '',
      author_name: author.username || author.name || '',
      timestamp: msg.timestamp || msg.createdTimestamp || new Date().toISOString(),
      channel_id: msg.channel_id || msg.channelId || (msg.channel && msg.channel.id) || channelId,
      thread_id: msg.thread_id || msg.threadId || (msg.channel && (msg.channel.parent_id || msg.channel.parentId)) || '',
    };
  })
  .filter((m) => m.id);

if (normalized.length === 0) {
  return [];
}

const newestSeenId = normalized[0].id;
const nonBot = normalized.filter((m) => !m.author_bot);

const candidates = [];
for (const msg of nonBot) {
  if (lastSeen && msg.id === lastSeen) {
    break;
  }
  candidates.push(msg);
}

let toEmit = candidates;
if (!lastSeen) {
  toEmit = candidates.length > 0 ? [candidates[0]] : [];
}

toEmit = toEmit.reverse();

if (newestSeenId) {
  data[key] = newestSeenId;
}

return toEmit.map((m) => ({
  json: {
    id: m.id,
    content: m.content,
    author_id: m.author_id,
    author_name: m.author_name,
    timestamp: m.timestamp,
    channel_id: m.channel_id,
    thread_id: m.thread_id,
  },
}));
