const raw = String($env.DISCORD_THREAD_CHANNEL_IDS || '');
const ids = [...new Set(raw.split(',').map((v) => v.trim()).filter(Boolean))];
return ids.map((threadId) => ({ json: { thread_channel_id: threadId } }));

const data = getWorkflowStaticData('global');
const inputItems = $input.all();
const grouped = new Map();

for (const item of inputItems) {
  const msg = item.json || {};
  const author = msg.author || msg.user || {};
  const threadId = String(msg.thread_channel_id || msg.channel_id || msg.channelId || (msg.channel && msg.channel.id) || '');
  const messageId = msg.id ? String(msg.id) : (msg.message_id ? String(msg.message_id) : '');

  if (!threadId || !messageId) {
    continue;
  }

  const normalized = {
    thread_id: threadId,
    channel_id: threadId,
    answer_message_id: messageId,
    reply_to_message_id: msg.message_reference?.message_id || msg.reference?.messageId || '',
    author_bot: Boolean(author.bot),
    author_id: author.id ? String(author.id) : '',
    author_name: author.username || author.name || '',
    content: typeof msg.content === 'string' ? msg.content : '',
    timestamp: msg.timestamp || msg.createdTimestamp || new Date().toISOString(),
  };

  if (!grouped.has(threadId)) {
    grouped.set(threadId, []);
  }
  grouped.get(threadId).push(normalized);
}

const output = [];

for (const [threadId, messages] of grouped.entries()) {
  if (messages.length === 0) {
    continue;
  }

  const key = `lastSeenThread:${threadId}`;
  const lastSeen = data[key] ? String(data[key]) : null;
  const newestSeenId = messages[0].answer_message_id;
  const nonBot = messages.filter((m) => !m.author_bot);

  const candidates = [];
  for (const msg of nonBot) {
    if (lastSeen && msg.answer_message_id === lastSeen) {
      break;
    }
    candidates.push(msg);
  }

  let toEmit = candidates;
  if (!lastSeen) {
    toEmit = candidates.length > 0 ? [candidates[0]] : [];
  }

  toEmit = toEmit.reverse();

  for (const msg of toEmit) {
    output.push({
      json: {
        thread_id: msg.thread_id,
        channel_id: msg.channel_id,
        answer_message_id: msg.answer_message_id,
        reply_to_message_id: msg.reply_to_message_id || '',
        author_id: msg.author_id,
        author_name: msg.author_name,
        content: msg.content,
        timestamp: msg.timestamp,
      },
    });
  }

  if (newestSeenId) {
    data[key] = newestSeenId;
  }
}

return output;
